import { TextField } from "@mui/material";
import React from "react";
import { useState } from "react";
import { Button, isTyping } from "react-chat-engine";
import { useMutation } from "react-query";
import { sendChat } from "server/api/chat";

const MessageForm = ({ chatId, creds }) => {
  const [value, setValue] = useState("");

  const mutationCreate = useMutation("sendMessage", (text) =>
    sendChat(chatId, text)
  );

  const handleSubmit = (e) => {
    e.preventDefault();
    const text = value.trim();

    if (text.length > 0) mutationCreate.mutate(text);
  };

  const handleChange = (e) => {
    setValue(e.target.value);
  };

  return (
    <form className="message-form" onSubmit={handleSubmit}>
      <TextField
        fullWidth
        className="message-input"
        value={value}
        onChange={handleChange}
        onSubmit={handleSubmit}
        variant="outlined"
        placeholder="Type a new message"
        sx={{ bottom: 0 }}
      />
      <Button>Send</Button>
    </form>
  );
};

export default MessageForm;
