import React from "react";
import { Avatar, AvatarGroup, Box, Grid, Typography } from "@mui/material";
import PersonOutlineOutlinedIcon from "@mui/icons-material/PersonOutlineOutlined";

const GroupIcon = () => {
  return (
    <>
      <Avatar>
        <PersonOutlineOutlinedIcon />
      </Avatar>
      <Avatar ml="-20px" bgcolor="primary.main">
        <PersonOutlineOutlinedIcon />
      </Avatar>
    </>
  );
};

const PersonalIcon = ({ name }) => {
  function stringToColor(string) {
    let hash = 0;
    let i;

    /* eslint-disable no-bitwise */
    for (i = 0; i < string.length; i += 1) {
      hash = string.charCodeAt(i) + ((hash << 5) - hash);
    }

    let color = "#";

    for (i = 0; i < 3; i += 1) {
      const value = (hash >> (i * 8)) & 0xff;
      color += `00${value.toString(16)}`.slice(-2);
    }
    /* eslint-enable no-bitwise */

    return color;
  }

  function stringAvatar(name) {
    return {
      sx: {
        bgcolor: stringToColor(name),
      },
      children: `${name.split(" ")[0][0]}`,
    };
  }

  console.log("disini");
  return <Avatar {...stringAvatar(name)} />;
};

const ChatCard = ({ groupChat }) => {
  console.log("groupChat", groupChat);
  return (
    <Box display="flex" mt="26px" alignItems="center">
      <Box width="34px" mr="17px">
        {groupChat ? <GroupIcon /> : <PersonalIcon name="FastVisa Support" />}
      </Box>
      <Box>
        <Grid container>
          <Grid item>
            <Box display="flex">
              <Typography
                variant="caption"
                sx={{ color: "primary.main", fontWeight: "bold" }}
              >
                109220-Naturalization
              </Typography>
              <Typography variant="caption" ml="16px">
                January 1,2021 19:1
              </Typography>
            </Box>
            <Box display="grid">
              <Typography variant="caption" sx={{ fontWeight: "bold" }}>
                Cameron Philips:
              </Typography>
              <Typography variant="caption">Please, Check This Out</Typography>
            </Box>
          </Grid>
        </Grid>
      </Box>
    </Box>
  );
};

export default ChatCard;
