import React from "react";
import {
  CircularProgress,
  InputAdornment,
  OutlinedInput,
  Box,
  Typography,
} from "@mui/material";
import SearchIcon from "@mui/icons-material/Search";
import { useQuery } from "react-query";
import { getList } from "server/api/chatList";

const LoadingComponent = () => {
  return (
    <>
      <CircularProgress color="inherit" />
      <Typography align="center">Loading Chats...</Typography>
    </>
  );
};

const ChatList = () => {
  const { data, isLoading } = useQuery(["dataList, chats"], () => getList());

  console.log("data", data);
  console.log("isLoading", isLoading);

  return (
    <Box pt="20px" pl="29px" pr="39px">
      <OutlinedInput
        fullWidth
        placeholder="Search"
        id="outlined-adornment-password"
        endAdornment={
          <InputAdornment position="end">
            <SearchIcon />
          </InputAdornment>
        }
        size="small"
        sx={{ height: "32px" }}
      />
      {isLoading ? <LoadingComponent /> : <h1>Disini List</h1>}
    </Box>
  );
};

export default ChatList;
