import React from "react";
import {
  CircularProgress,
  InputAdornment,
  OutlinedInput,
  Box,
  Typography,
  Grid,
} from "@mui/material";
import SearchIcon from "@mui/icons-material/Search";
import { useQuery } from "react-query";
import { getList } from "server/api/chatList";

const LoadingComponent = () => {
  return (
    <Grid container>
      <Grid item margin="auto" textAlign="center">
        <CircularProgress color="inherit" />
        <Typography align="center">Loading Chats...</Typography>
      </Grid>
    </Grid>
  );
};

const ChatList = () => {
  const { data, isLoading } = useQuery(["dataList, chats"], () => getList());

  console.log("data", data);
  console.log("isLoading", isLoading);

  return (
    <Box height="100%" pt="20px" pl="29px" pr="39px">
      <OutlinedInput
        fullWidth
        placeholder="Search"
        id="outlined-adornment-password"
        endAdornment={
          <InputAdornment position="end">
            <SearchIcon />
          </InputAdornment>
        }
        size="small"
        sx={{ height: "32px" }}
      />
      <Box height="90%">
        {isLoading ? <LoadingComponent /> : <LoadingComponent />}
      </Box>
    </Box>
  );
};

export default ChatList;
