import chatsJSON from "server/data/chats";

export default (req, res) => {
  console.log("req", req);

  const chatID = Number(req.query.id);
  const chatList = chatsJSON.id;

  console.log("chatID", chatID);
  const chatBasedOnID = chatList.filter((chat) => chat.id === chatID);
  console.log("chatBasedOnID", chatBasedOnID);

  if (chatBasedOnID.messages.chats.length > 0) {
    res.statusCode = 200;
    res.json(chatBasedOnID.messages.chats.pop());
  } else {
    res.statusCode = 404;
    res.send("Not found - ID is not related to any products");
  }
};
