import React from "react";
import {
  AppBar,
  Box,
  Container,
  CssBaseline,
  Grid,
  Toolbar,
  Typography,
} from "@mui/material";
import ArrowBackIcon from "@mui/icons-material/ArrowBack";
import CloseIcon from "@mui/icons-material/Close";
import MessageHeader from "../ChatComponent/MessageHeader";
import { useQuery } from "react-query";
import { chatFeed } from "server/api/chat";
import MyMessage from "../ChatComponent/MyMessage";
import TheirMessage from "../ChatComponent/TheirMessage";
import { useEffect } from "react";

const ChatFeed = ({ setLayout, messageId }) => {
  console.log("id", messageId);

  const { data, isLoading } = useQuery(
    ["dataList", "chatFeed", messageId],
    () => chatFeed(messageId)
  );

  const renderMessages = () => {
    data.map((message, index) => {
      const lastMessageKey = index === 0 ? null : message?.id;
      const isMysMessage = message?.sender_username === "Michelle";
    });

    return (
      <Box key={`msg${index}`} width="100%">
        <Box className="message-block">
          {isMyMessage ? <MyMessage /> : <TheirMessage />}
        </Box>
      </Box>
    );
  };

  useEffect(() => {
    renderMessages();
  }, []);

  console.log("data", data);

  if (isLoading) return <b>Tunggu Ya</b>;

  return (
    <Box display="flex">
      <MessageHeader />
      <Box component="main">
        <Toolbar />
        <Box className="chat-feed">
          <Box className=""></Box>
        </Box>
      </Box>
    </Box>
  );
};

export default ChatFeed;
